import { TextInput, Button} from 'react-native-paper';
import logo from '../assets/R.png';
import axios from 'axios';
import metal from '../assets/2.jpeg';

import React,{useState} from 'react';
import { ImageBackground, StyleSheet, Text, Image, View, SafeAreaView, TouchableOpacity, Dimensions} from 'react-native';
const StartupScreen= ({ navigation }) => {
  const [email, setEmail] = useState({ value: ''});
  const windowHeight = Dimensions.get('window').height;
  var isLogged = useState(false);

  function logInAuthentification()  {
    let employee_email = email.value;
    if(employee_email == "") return alert("Email is Required !!");
    console.log (employee_email);
    //Call API
    return axios.get(`https://rocketmobile2000.herokuapp.com/api/employees/${employee_email}`)
        .then(function (response) {
          console.log ('API inside');
          console.log (response.data);
            const answear = response.data;
            if (answear == true) {
              console.log ('works');
              navigation.reset({
                                index: 0,
                                routes: [{ name: "Elevator List", isLogged: true }],
                            })
            }
            else{
              console.log("Email is incorrect");
              alert("Sorry! the entered email  is not a valid listed agent");
            }
        })
      }
 

  return (
    <View style={styles.container}>
      <ImageBackground  style={styles.background} source={require('../assets/2.jpeg')}>
      <Image Image source={logo} style={styles.logo} resizeMode="contain"  />
        <View View style={styles.viewStyle}>
          <View style={{height:70}}>
          
          </View>
          <View style={{height:(windowHeight*0.1)}}/>
        <View>
          <Text style={styles.exemple}> Welcome to Rocket Elevators</Text>
        </View>
        </View>
        
        <View>
          <Text style = {styles.example}>Please Enter Your Email address</Text>
          <TextInput    style={styles.input}
          value={email.value}  onChangeText={(text) => setEmail({ value: text})}  required />
          <TouchableOpacity>
            <Button  style={styles.button}
            icon="login" mode="contained" onPress={logInAuthentification} > Log in </Button>
          </TouchableOpacity>
        </View>
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
       
        alignItems: 'center',
        justifyContent: 'center',
        borderBottomWidth: '1',
        borderBottomColor: '#eee'
    },
    title: {
        fontSize: 18,
        margin: 10,
        textAlign: 'center',
        color: 'rgb(34, 65, 115)',
  },
  exemple:{
    color:'#fff', 
    fontSize:50, 
    textAlign:'center'
  },
  example:{
    color:'white', 
    fontSize:20, 
    textAlign:'center'
  },
     logo: {
    width: 305,
    height: 159,
    resizeMode: "contain",
    marginBottom: 30,
    marginTop: 0,
  },
    elevatorList: {
        fontSize: 15,
        margin: 10,
        textAlign: 'center',
        color: 'rgb(34, 65, 115)',
        borderBottomWidth: '1',
        borderBottomColor: '#eee'
    },
    button: {
        marginTop: 20,
        elevation: 8,
        backgroundColor: "blue",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
    },
    buttonText: {
        fontSize: 15,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
    }
  });
export default StartupScreen;
