import React, { useState } from 'react';
import axios from 'axios';
import logo from '../assets/R2.png'
import { StyleSheet, Text, View, Image, TextInput, TouchableOpacity } from 'react-native';


const login = (props) => {
  const [id] = useState([])
    const [email, setEmail] = useState('');
    const authentication = async() => {
        return axios.get(`https://rocketmobile2000.herokuapp.com/api/employees`)
            .then(function (response) {
                if (response.status == 200) {
                  props.navigation.replace('Home');
                }
            })
            .catch(function (error) {
                console.log(`Please enter a valid email`);
                alert(`Please enter a valid email`);
            })
    }
      return (
        <View style={styles.container}>
            <View style={styles.viewStyle}>
                <Image source={logo} style={styles.logo} />
                <Text style={styles.insertEmail}>Enter a valid email to login</Text>
                <TextInput
                        placeholder='Enter your Rocket Elevators email'
                        style={styles.input}
                        autoCapitalize="none"
                        onChangeText={(emailvalue) => {
                            setEmail(emailvalue);
                        }}
                        value={email}
                    />
                    <TouchableOpacity
                        onPress={() => authentication()}
                        style={styles.button}>
                        <Text style={styles.buttonText}>Login</Text>
                    </TouchableOpacity>
            </View>
        </View>
      )
}
const styles = {
  container: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
      borderBottomWidth: '1',
      borderBottomColor: '#eee'
  },
  title: {
      fontSize: 18,
      margin: 10,
      textAlign: 'center',
      color: 'rgb(34, 65, 115)',
},
   logo: {
  width: 305,
  height: 159,
  resizeMode: "contain",
  marginBottom: 30,
  marginTop: 0,
},
  elevatorList: {
      fontSize: 15,
      margin: 10,
      textAlign: 'center',
      color: 'rgb(34, 65, 115)',
      borderBottomWidth: '1',
      borderBottomColor: '#eee'
  },
  button: {
      marginTop: 20,
      elevation: 8,
      backgroundColor: "#335e82",
      borderRadius: 10,
      paddingVertical: 10,
      paddingHorizontal: 12
  },
  buttonText: {
      fontSize: 15,
      color: "#fff",
      fontWeight: "bold",
      alignSelf: "center",
      textTransform: "uppercase"
  }
}
export default login;
