import 'react-native-gesture-handler';
import React,{useState,useEffect} from 'react';
import { ActivityIndicator,ImageBackground,Image, StyleSheet, Text, View, TouchableOpacity, ScrollView} from 'react-native';
import {  Appbar, Button} from 'react-native-paper';


const ElevatorStatusScreen=(props)=> {

    const { id } = props.route.params;
    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState([]);

// function checkStatus() {
useEffect(()=>{
      fetch(`https://rocketmobile2000.herokuapp.com/api/elevators/${id}`)
        .then((response) => response.json())
        .then((json) => setData(json))
        .catch((error) => console.error(error))
        .finally(() => setLoading(false));                      
    }, [data]);

useEffect(() => {
        return () => {
          console.log("cleaned up");
        };
      }, []);



function updateStatus() {
    fetch(`https://rocketmobile2000.herokuapp.com/api/elevators/${id}`, {
        method: 'POST',
        headers: new Headers({
                   'Content-Type': ' application/json', //  Content-Type
          }),
        body: JSON.stringify({
            status: 'Active'
          })
          // <-- Post parameters
      })
      .then((response) => response.text())
      .then((responseText) => {
        alert(responseText)
        
      })
      .catch((error) => {
          console.error(error);
      });
    };

   console.log("here")
    return (
        <View>
              <TouchableOpacity>



{isLoading ? <ActivityIndicator/> : (
    <Text  style={[
        styles.status,
        data.status == "Inactive" ?
        { backgroundColor: 'red' } 
        : { backgroundColor: 'green' }
    ]}>
{data.status}
  </Text>

  )}


  

      <Button style={styles.buttonText}
    icon="switch" mode="outlined" onPress={() => updateStatus()}>
 End Task
  </Button>  



<Button style={styles.buttonText}
    icon="logout" mode="outlined" onPress={() => props.navigation.navigate("Elevator List")}>
 Back
  </Button>  
  </TouchableOpacity>
   {/* </ImageBackground> */}
</View>
    );
}
// }

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    borderBottomWidth: '1',
    borderBottomColor: '#eee'
   },
    instructions: {
      ontSize: 18,
      margin: 10,
      textAlign: 'center',
      color: 'rgb(34, 65, 115)',
  
    }, 
    button: {
      marginTop: 20,
      elevation: 8,
      backgroundColor: "#335e82",
      borderRadius: 10,
      paddingVertical: 10,
      paddingHorizontal: 12
  },
  buttonText: {
    fontSize: 15,
    color: "#fff",
    fontWeight: "bold",
    alignSelf: "center",
    textTransform: "uppercase"
},
    textinput: {
      fontSize:30, height: 40,
      margin: 10,
      color: "#fff",

    },
    elevatorId: {
      backgroundColor: "red",
      padding: 20,
      borderRadius: 5,
      textAlign: "center",
      borderBottomWidth: '1',
      borderBottomColor: 'rgb(24, 36, 56)'
    },
    elevatorIdText: {
      fontSize: 20,
      color: '#rgb(24, 36, 56)',
  
    },
    fab: {
        position: 'absolute',
        margin: 16,
        right: 10,
        top: 130
        ,
      },
      image: {
        flex: 1,
        resizeMode: "cover",
        justifyContent: "center"
      }
  });

  export default ElevatorStatusScreen;
