import { TextInput, Button} from 'react-native-paper';
import Logo from '../assets/R2.png';
import axios from 'axios';
import metal from '../assets/jpeg';

import React,{useState} from 'react';
import { ImageBackground, StyleSheet, Text, Image, SafeAreaView, TouchableOpacity} from 'react-native';
const StartupScreen= ({ navigation }) => {
  const [email, setEmail] = useState({ value: ''})

  function logInAuthentification()  {
    let employee_email = email.value;
    if(employee_email == "") return alert("Email is Required !!");
    console.log (employee_email);
    //Call API
    return axios.get(`https://tr-api-rest.herokuapp.com/api/employee/${employee_email}`)
        .then(function (response) {
          console.log ('API inside');
          console.log (response.data);
            const answear = response.data;
            if (answear == true) {
              console.log ('works');
              navigation.reset({
                                index: 0,
                                routes: [{ name: "Home" }],
                            })
            }
            else{
              console.log("${employee_email} is incorrect");
              alert("Sorry!\n${employee_email} is not the email of a listed agent");
            }
        })
      }
 

  return (
    <ImageBackground source={metal} style={styles.image}>
      <SafeAreaView style={styles.container}>
        <Image source={Logo} style={styles.logo} resizeMode="contain" /> 
        <Text style={styles.instructions}> Welcome to Rocket Elevators!</Text>
        <Text style = {styles.instructions1}>Please to start login using your email</Text>
        <TextInput style={styles.input} value={email.value}  onChangeText={(text) => setEmail({ value: text})}  required />
        <TouchableOpacity>
          <Button style = {styles.button}
          icon="login" mode="contained" onPress={logInAuthentification} > Log in </Button>
        </TouchableOpacity>
      </SafeAreaView>
    </ImageBackground>
  );
}
export default StartupScreen;
