import 'react-native-gesture-handler';
import React,{useState,useEffect} from 'react';
import { ActivityIndicator,FlatList,Image, StyleSheet, Text, View, TouchableOpacity, ImageBackground, ScrollView} from 'react-native';
import {  FAB, Appbar, Button} from 'react-native-paper';


const ElevatorScreen=(props)=> {


        const [isLoading, setLoading] = useState(true);
        const [data, setData] = useState([]);

      useEffect(()=>{
          fetch('https://rocketmobile2000.herokuapp.com/api/elevators/notActive')
            .then((response) => response.json())
            .then((json) => setData(json))
            .catch((error) => console.error(error))
            .finally(() => setLoading(false));
        }, []);


    return (
        <View>


<Button icon="logout" mode="contained" onPress={() => props.navigation.navigate('Sign In')}>
   Log Out!
  </Button>

  <TouchableOpacity>
  
        
{isLoading ? <ActivityIndicator/> : (

        <FlatList
          data={data}
          keyExtractor={({ id }, index) => id}
          renderItem={({ item }) => (
            <Button style={styles.buttonText}
            icon="login" mode="outlined" onPress={() => {
                // onPress event fires with an event object
                props.navigation.navigate('Elevator Status', {

                    id: item.id ,
                    status: item.status

                  });
            }}>
         {item.id} 
         {item.status}
          </Button>

          )}
        />
      )}


  </TouchableOpacity>

         </View>
    )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    borderBottomWidth: '1',
    borderBottomColor: '#eee'
},
    instructions: {
      ontSize: 18,
      margin: 10,
      textAlign: 'center',
      color: 'rgb(34, 65, 115)',
  
    }, 
    button: {
      marginTop: 20,
      elevation: 8,
      backgroundColor: "#335e82",
      borderRadius: 10,
      paddingVertical: 10,
      paddingHorizontal: 12
  },
  buttonText: {
    fontSize: 15,
    color: "#fff",
    fontWeight: "bold",
    alignSelf: "center",
    textTransform: "uppercase"
},
    textinput: {
      fontSize:30, height: 40,
      margin: 10
    },
    elevatorId: {
      backgroundColor: "red",
      padding: 20,
      borderRadius: 5,
      textAlign: "center"
    },
    elevatorIdText: {
      fontSize: 20,
      color: '#fff',
  
    },
    fab: {
        position: 'absolute',
        margin: 16,
        right: 10,
        top: 130
        ,
      },
      image: {
        flex: 1,
        resizeMode: "cover",
        justifyContent: "center"
      }
  });

export default ElevatorScreen;
